import renoir.test.geoclef.pertopic.GeoCLEF_PerTopic_Test
import groovy.util.GroovyTestCase
import org.apache.log4j.Logger
import saskia.bin.Configuration
import renoir.obj.*
import rembrandt.gazetteers.CommonClassifications as SC
/**
 * @author Nuno Cardoso
 *
 */
 class GeoCLEF_EN_2006_026_Test extends GroovyTestCase {
        
    Configuration conf 
    GeoCLEF_PerTopic_Test pertopic
    static Logger log = Logger.getLogger("JUnitTest")

    public GeoCLEF_EN_2006_026_Test() {
		conf = Configuration.newInstance()
		log.info "DBpedia sparql service set to "+conf.get("saskia.dbpedia.url","none")	
		pertopic = new GeoCLEF_PerTopic_Test(conf)
    }
	
    void testTopic() {
		String lang = "en"
		String topic = "label:026 wine regions zones in rivers in Europe"

		RenoirQuery rq = RenoirQueryParser.parse(topic)
		Question question = pertopic.process(rq, lang)
		//dumpQuestion(question)
     
		/** check sentences and question types */
		assert question.sentence*.text == ['wine','regions','zones','in','rivers','in','Europe']
		assert question.questionType == QuestionType.None
		assert question.questionTypeTerms.size() == 0
	
/*  select * from subject where sbj_subject like "%ine][region%";
+--------+--------------------+
| sbj_id | sbj_subject        |
+--------+--------------------+
|   6340 | en:[Wine][regions] | 
+--------+--------------------+
1 row in set (0.05 sec)

*/

	// check detected NEs 
		assert question.nes.size() == 1
		assert question.nes[0].terms*.text == ['Europe']
    
		/// contains gives false. Use find
		assert question.nes[0].classification.find{it == SC.place_human_division}
		
	// check subjects 
		assert question.subject.subjectTerms*.text == ['wine','regions']

	// check conditions 
		assert question.conditions.size() == 1
		assert question.conditions[0].object instanceof Subject
		assert question.conditions[0].object.subjectTerms*.text == ["rivers"]
		assert question.conditions[0].object.geoscopeTerms*.text == ["Europe"]
	
	// SPARQL should resolve instances of the SUBJECT against the condition 
	// QueryGeoscope, using dbpedia-owl:location
	
	// Then, for the lone condition Subject, we resolve it and add it 			
		assert question.expectedAnswerTypes.size() == 1
		assert question.expectedAnswerTypes[0].DBpediaOntologyResources == \
		   question.subject.categoryWikipediaAsDBPediaResource
		assert question.expectedAnswerTypes[0].resolvesTo == ExpectedAnswerType.Type.DBpediaOntologyResource
		
	// check answers 
